exports.get = getItem;
exports.store = storeItem;

var registry = {};

// TODO: when can we remove the item from the registry?

function getItem (name) {
	var item;

	item = registry[name];
	if (!item) throw new Error('Unknown module: ' + name);

	if (!'value' in item) {
		// TODO: evaluate
	}

	return item.value;
}

function storeItem (item) {
	registry[item.name] = item;
	return item;
}
